INSERT INTO super_hero (name, super_hero_name, description, comic) VALUES ('Peter Parker', 'Spider-man', 'Fuerza, rapidez y reflejos proporcionales a una araña. Capacidad de adherirse a casi cualquier superficie.', 'Marvel');
INSERT INTO super_hero (name, super_hero_name, description, comic) VALUES ('Steve Rogers', 'Captain America', 'Sentidos, agilidad, velocidad y fuerza sobrehumanas. Gran habilidad con armas de fuego, con el martillo mjolnir y con su escudo. Instinto de liderazgo, gran resistencia, inmune a gases y enfermedades, curación y regeneración acelerada. Genio táctico, artista marcial, acróbata experto', 'Marvel');
INSERT INTO super_hero (name, super_hero_name, description, comic) VALUES ('Bruce Banner', 'Hulk', 'Invulnerabilidad. Longevidad. Regeneración. Capacidad de respirar bajo el agua, respirar en el espacio y ver fantasmas y otras entidades astrales.', 'Marvel');
INSERT INTO super_hero (name, super_hero_name, description, comic) VALUES ('Tony Stark', 'Iron Man', 'Fuerza sobrehumana y durabilidad. Vuelo supersónico. Repulsor de energía y misiles de proyección. Regenerativo soporte vital. Uso de equipamiento, dispositivos y armamento de alta tecnología.', 'Marvel');
INSERT INTO super_hero (name, super_hero_name, description, comic) VALUES ('Bruce Wayn', 'Batman', 'Habilidades como detective. Intelecto nivel genio.​ Brillante estratega y acróbata experto. Dominio de artes marciales y técnicas de sigilo e intimidación. Escapismo. Uso de equipamiento, dispositivos y armamento de alta tecnología.', 'DC Comics');
INSERT INTO super_hero (name, super_hero_name, description, comic) VALUES ('Clark Kent', 'Superman', 'Fuerza sobrehumana, velocidad, resistencia, agilidad, reflejos, durabilidad, sentidos y longevidad. Poderes oculares. Agudeza sobrehumana. Visión de calor. Visión del espectro electromagnético. Visión microscópica. Aliento sobrehumano. Invulnerabilidad. Volar.', 'Marvel');
INSERT INTO super_hero (name, super_hero_name, description, comic) VALUES ('James Logan', 'Wolverine', 'Es un mutante que posee sentidos afinados a los animales, capacidades físicas mejoradas, poderosa capacidad de regeneración conocida como un factor de curación, y tres garras retráctiles en cada mano', 'Marvel');
INSERT INTO super_hero (name, super_hero_name, description, comic) VALUES ('Reed Richards', 'Mr Fantastic', 'Genio científico, puede estirar, girar, y re-formar su cuerpo a proporciones inhumanas', 'Marvel');
INSERT INTO super_hero (name, super_hero_name, description, comic) VALUES ('Susan Storm', 'Invisible woman', 'Capacidad de doblarse y manipular la luz para hacerse invisibles a ella misma ya otras personas', 'Marvel');
INSERT INTO super_hero (name, super_hero_name, description, comic) VALUES ('Johnny Storm', 'Human Torch', 'Posee la habilidad de controlar el fuego, permitiéndole proyectar fuego desde su cuerpo, así como el poder de volar', 'Marvel');
INSERT INTO super_hero (name, super_hero_name, description, comic) VALUES ('Ben Grimm','The Thing', 'Monstruoso y descarpado humanoide con piel naranja de roca y superfuerza', 'Marvel');
